import './App.css';
import { useState} from 'react';
import Plus from './UI/Plus';
import Trash from './UI/Trash';
import Edit from './UI/Edit';

function App() {

  const [boards, setBoards] = useState([{title: 'To Do', tasks: [{id: 0, body: 'wash the dishes'}, {id: 1, body: 'take out trash'}, {id: 2, body: 'portfolio'}, {id: 3, body: 'job search'}, {id: 4, body: 'learn Spanish'}, {id:5, body: 'buy tickets'}]}, {title: 'In Progress', tasks: [{id: 12, body: 'sleeping'}]}, {title: 'Done', tasks: [{id:13, body: 'reading'}]}])
  const newTasks = [...boards]


  //works
  function addTask(e, board) {
    newTasks[newTasks.indexOf(board)].tasks.push({id: Math.round(Math.random()*100), body: 'New task'})
    setBoards(newTasks)
  }

  //doesn't work
  function removeTask(e, board, task) {
    newTasks[newTasks.indexOf(board)].tasks.filter(item => item !== task)
    setBoards(newTasks)
  }

  return (
    <div className='flex center'>
      {boards.map(board => {
        return (
          <div
            key={board.title} 
            className='board'
            id={board.title}
          >
            <h2>{board.title}</h2>
            {board.tasks.map( task => {
              return (
              <div 
                key={task.id}
                className='task flex center'
                /* draggable */
              >
                {task.body}
                <Trash handleClick={(e) => removeTask(e,board, task)}/>
                <Edit />
              </div>)
            })}
            <Plus handleClick={(e) => addTask(e, board)}/>
          </div>)
        })}
    </div>
  );
}

export default App;
