import './App.css';
import { useState} from 'react';
import Plus from './UI/Plus';
import Trash from './UI/Trash';
import Edit from './UI/Edit';

function App() {

  const [boards, setBoards] = useState([
    {title: 'To Do', tasks: [{id: 0, body: 'wash the dishes'}, {id: 1, body: 'take out trash'}, {id: 2, body: 'portfolio'}, {id: 3, body: 'job search'}, {id: 4, body: 'learn Spanish'}, {id: 5, body: 'buy tickets'}]}, 
    {title: 'In Progress', tasks: [{id: 12, body: 'sleeping'}]}, 
    {title: 'Done', tasks: [{id: 13, body: 'reading'}]}]
  )
  let newTasks = [...boards]


  //works
  function addTask(e, board) {
    newTasks[newTasks.indexOf(board)].tasks.push({id: Math.round(Math.random()*10000), body: 'New task'})
    setBoards(newTasks)
  }

  function removeTask(e, board, task) {
    e.stopPropagation()
    newTasks[newTasks.indexOf(board)].tasks.splice(newTasks.indexOf(task), 1)
    setBoards(newTasks)
  }

  function editTask(e, task) {

  }

  return (
    <div className='flex center-hor'>
      {boards.map(board => {
        return (
          <div
            key={board.title} 
            className='board'
            id={board.title}
            style={{height: 'fit-content', transition: '0.5s'}}
          >
            <h2>{board.title}</h2>
            {board.tasks.map( task => {
              return (
              <div 
                key={task.id}
                className='task flex center-ver'
                draggable
              >
                <span className='task-body'>{task.body}</span>
                <div className='actions'>
                  <Trash handleClick={(e) => removeTask(e, board, task)}/>
                  <Edit handleClick={(e) => editTask(e,task)}/>
                </div>
              </div>)
            })}
            <Plus handleClick={(e) => addTask(e, board)}/>
          </div>)
        })}
    </div>
  );
}

export default App;
