import './App.css';
import { useState} from 'react';


function App() {

  const [boards, setBoards] = useState([{title: 'To Do', 
        tasks: [{id: 0, body: 'wash the dishes'}, {id: 1, body: 'take out trash'}, {id: 2, body: 'portfolio'}, {id: 3, body: 'job search'}, {id: 3, body: 'learn Spanish'}, {id:4, body: 'buy tickets'}]}])


  return (
    <div>
      {boards.map( board => {
        return (
          <div 
            className='board'
            id={board.title}
          >
            <h2>{board.title}</h2>
            {board.tasks.map( task => {
              return (
              <div>
                {task.body}
              </div>)
            })}
          </div>)
        })}
    </div>
  );
}

export default App;
