import './App.css';
import { useState} from 'react';
import Plus from './UI/Plus';
import Trash from './UI/Trash';
import Edit from './UI/Edit';
import Tick from './UI/Tick';
import Cancel from './UI/Cancel';

function App() {

  const [boards, setBoards] = useState([
    {title: 'To Do', tasks: [{id: 0, body: 'wash the dishes', edit: false}, {id: 1, body: 'take out trash', edit: false}, {id: 2, body: 'portfolio', edit: false}, {id: 3, body: 'job search', edit: false}, {id: 4, body: 'learn Spanish', edit: false}, {id: 5, body: 'buy tickets', edit: false}]}, 
    {title: 'In Progress', tasks: [{id: 12, body: 'sleeping', edit: false}]}, 
    {title: 'Done', tasks: [{id: 13, body: 'reading', edit: false}]}]
  )
  let newTasks = [...boards]

  //works
  function addTask(e, board) {
    newTasks[newTasks.indexOf(board)].tasks.push({id: Math.round(Math.random()*10000), body: 'New task'})
    setBoards(newTasks)
  }

  function removeTask(e, board, task) {
    e.stopPropagation()
    newTasks[newTasks.indexOf(board)].tasks.splice(newTasks.indexOf(task), 1)
    setBoards(newTasks)
  }

  function editTaskName(e, board, task) {
    e.stopPropagation()
    const boardIndex = newTasks.indexOf(board)
    const taskIndex = newTasks[boardIndex].tasks.indexOf(task)
    newTasks[boardIndex].tasks[taskIndex].edit = true
    setBoards(newTasks)
  }

  function saveTaskName(e, board, task) {
    e.stopPropagation()
    const boardIndex = newTasks.indexOf(board)
    const taskIndex = newTasks[boardIndex].tasks.indexOf(task)
    newTasks[boardIndex].tasks[taskIndex].edit = false
    setBoards(newTasks)
  }

  function cancelChangeTaskName() {

  }

  function changeTaskName() {

  }

  return (
    <div className='flex center-hor'>
      {boards.map(board => {
        return (
          <div
            key={board.title} 
            className='board'
            id={board.title}
          >
            <h2>{board.title}</h2>
            {board.tasks.map( task => {
              return (
              <div 
                key={task.id}
                className='task flex center-ver'
                draggable
              >
                {task.edit && 
                  <input 
                    placeholder={task.body}
                    onChange={(e) => changeTaskName(e, board, task)} 
                  ></input>
                }
                {!task.edit && <span className='task-body'>{task.body}</span>}
                <div className='actions'>
                  {!task.edit && <Trash handleClick={(e) => removeTask(e, board, task)}/>}
                  {!task.edit && <Edit handleClick={(e) => editTaskName(e, board, task)}/>}
                  {task.edit && <Cancel handleClick={(e) => cancelChangeTaskName(e, board, task)}/>}
                  {task.edit && <Tick handleClick={(e) => saveTaskName(e, board, task)}/>}
                </div>
              </div>)
            })}
            <Plus handleClick={(e) => addTask(e, board)}/>
          </div>)
        })}
    </div>
  );
}

export default App;
