import './App.css';
import { useState} from 'react';
import Plus from './UI/Plus';
import Trash from './UI/Trash';
import Edit from './UI/Edit';

function App() {

  const [boards, setBoards] = useState([
    {title: 'To Do', tasks: [[ 0,'wash the dishes'], [ 1,'take out trash'], [ 2,'portfolio'], [ 3,'job search'], [ 4,'learn Spanish'], [5,'buy tickets']]}, 
    {title: 'In Progress', tasks: [[ 12,'sleeping']]}, 
    {title: 'Done', tasks: [[13,'reading']]}])
  const newTasks = [...boards]


  //works
  function addTask(e, board) {
    newTasks[newTasks.indexOf(board)].tasks.push([Math.round(Math.random()*100),'New task'])
    setBoards(newTasks)
  }

  //doesn't work
  function removeTask(e, board, task) {
    e.stopPropagation()
    setBoards(oldBoards => oldBoards.map(oldBoard => oldBoard.tasks.filter(item => item !== task)))
  }

  return (
    <div className='flex center'>
      {boards.map(board => {
        return (
          <div
            key={board.title} 
            className='board'
            id={board.title}
          >
            <h2>{board.title}</h2>
            {board.tasks.map( task => {
              return (
              <div 
                key={task[0]}
                className='task flex center'
                /* draggable */
              >
                {task[1]}
                <Trash handleClick={(e) => removeTask(e,board, task)}/>
                <Edit />
              </div>)
            })}
            <Plus handleClick={(e) => addTask(e, board)}/>
          </div>)
        })}
    </div>
  );
}

export default App;
