import './App.css';
import { useState} from 'react';
import Plus from './UI/Plus';
import Trash from './UI/Trash';

function App() {

  const [boards, setBoards] = useState([{title: 'To Do', tasks: [{id: 0, body: 'wash the dishes'}, {id: 1, body: 'take out trash'}, {id: 2, body: 'portfolio'}, {id: 3, body: 'job search'}, {id: 4, body: 'learn Spanish'}, {id:5, body: 'buy tickets'}]}, {title: 'In Progress', tasks: []}, {title: 'Done', tasks: []}])



  function addTask(e, board) {
    const newTasks = [...boards]
    newTasks[newTasks.indexOf(board)].tasks.push({id: Math.round(Math.random()*100), body: 'New task'})
    setBoards(newTasks)
  }

  return (
    <div className='flex center'>
      {boards.map( board => {
        return (
          <div
            key={board.title} 
            className='board'
            id={board.title}
          >
            <h2>{board.title}</h2>
            {board.tasks.map( task => {
              return (
              <div 
                key={task.id}
                className='task'
                draggable
              >
                {task.body}
                <Trash handleClick={(e) => removeTask(e, board, task)}/>
              </div>)
            })}
            <Plus handleClick={(e) => addTask(e, board)}/>
          </div>)
        })}
    </div>
  );
}

export default App;
